import axios from 'axios';
import React, { useState } from 'react'
import Context from '../../Context';
import withLayout from '../hoc/withLayout'
import SearchLocation from './SearchLocation';
import WeatherInfo from './WeatherInfo';
import Error from './Error';


const API_KEY = "9a5a7ea3dd34950e979f334e007c1883";


const Home = () => {

    const [weather, setWeather] = useState();
    // const [error, setError] = useState();

    const api_call = async (e) =>{
        e.preventDefault();
        const location = e.target.elements.location.value;

        // if(!location){
        //     return setError('Please enter the correct city name');
        // }

        const baseUrl = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=${API_KEY}&units=metric`;
        
        const request = axios.get(baseUrl);
        const responce = await request;

        setWeather(responce.data);
    }
    weather && console.log(weather)

    // useEffect(()=>{
    //     api_call();
    // },[])

    return (
    <>
        <Context.Provider value={{api_call, weather}}>
            <SearchLocation />
            {weather && <WeatherInfo />}
            <Error />
		</Context.Provider>
    </>
  )
}

export default withLayout(Home)
